project "libcurl"
    language "C++"
    kind "StaticLib"

    includedirs
    {
        "include",
        "lib"
    }

    defines
    {
        -- We are building libcurl
        "BUILDING_LIBCURL",
        -- Build as a static library
        "CURL_STATICLIB",
        
        -- Enable IPV6 support
        "USE_IPV6",
        
        -- Enable Windows SSPI support
        "USE_WINDOWS_SSPI",
        "USE_SCHANNEL",
        
        -- Enable WinIDN support
        --"USE_WIN32_IDN",
        --"WANT_IDN_PROTOTYPES",
        
    }
    
    vpaths
    {
        ["Headers/*"] = { "include/curl/**.h", "lib/**.h" },
        ["Sources/*"] = "lib/**.c",
        ["*"] = "premake5.lua"
    }

